import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as Yup from "yup";
import { useLocation } from "react-router";

const AddEditRest = () => {
  const { state } = useLocation();
  const id = state.id;
  const isAddMode = !id;

  // form validation rules
  const validationSchema = Yup.object().shape({
    name: Yup.string().required("Name is required"),
    chefName: Yup.string().required("Chef Name is required"),
    pic: Yup.string().required("Picture is required"),
    dishes: Yup.string().required("Dishes are required"),
  });

  // functions to build form returned by useForm() hook
  const {
    register,
    handleSubmit,
    reset,
    setValue,
    getValues,
    errors,
    formState,
  }: any = useForm({
    resolver: yupResolver(validationSchema),
  });

  function onSubmit(data: any) {
    return isAddMode ? createRest(data) : updateRest(id, data);
  }

  const createRest = (data: any) => {
    // return userService.create(data)
    //     .then(() => {
    //         alertService.success('User added', { keepAfterRouteChange: true });
    //         history.push('.');
    //     })
    //     .catch(alertService.error);
  };

  const updateRest = (id: string, data: any) => {
    // return userService.update(id, data)
    //     .then(() => {
    //         alertService.success('User updated', { keepAfterRouteChange: true });
    //         history.push('..');
    //     })
    //     .catch(alertService.error);
  };

  // const [user, setUser] = useState({});
  // const [showPassword, setShowPassword] = useState(false);

  // useEffect(() => {
  //     if (!isAddMode) {
  //         // get user and set form fields
  //         userService.getById(id).then(user => {
  //             const fields = ['title', 'firstName', 'lastName', 'email', 'role'];
  //             fields.forEach(field => setValue(field, user[field]));
  //             setUser(user);
  //         });
  //     }
  // }, []);

  return (
    <form onSubmit={handleSubmit(onSubmit)} onReset={reset}>
      <h1>{isAddMode ? "Add Restaurant" : "Edit Restaurant"}</h1>
      <div className="form-row">
        <div className="form-group col-5">
          <label>Name</label>
          <input
            name="name"
            type="text"
            ref={register}
            className={`form-control ${errors.name ? "is-invalid" : ""}`}
          />
          <div className="invalid-feedback">{errors.firstName?.message}</div>
        </div>
        <div className="form-group col-5">
          <label>Chef Name</label>
          <input
            name="chefName"
            type="text"
            ref={register}
            className={`form-control ${errors.chefName ? "is-invalid" : ""}`}
          />
          <div className="invalid-feedback">{errors.lastName?.message}</div>
        </div>
        <div className="form-group col-5">
          <label>Picture</label>
          <input
            name="pic"
            type="text"
            ref={register}
            className={`form-control ${errors.pic ? "is-invalid" : ""}`}
          />
          <div className="invalid-feedback">{errors.lastName?.message}</div>
        </div>
        <div className="form-group col-5">
          <label>Dishes</label>
          <input
            name="dishes"
            type="text"
            ref={register}
            className={`form-control ${errors.dishes ? "is-invalid" : ""}`}
          />
          <div className="invalid-feedback">{errors.lastName?.message}</div>
        </div>
      </div>
      <div className="form-group">
        <button
          type="submit"
          disabled={formState.isSubmitting}
          className="btn btn-primary"
        >
          {formState.isSubmitting && (
            <span className="spinner-border spinner-border-sm mr-1"></span>
          )}
          Save
        </button>
        <Link to={isAddMode ? "." : ".."} className="btn btn-link">
          Cancel
        </Link>
      </div>
    </form>
  );
};

export default AddEditRest;
