import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Controller, useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as Yup from "yup";
import { useLocation } from "react-router";
import Input from "@mui/material/Input";
import Button from "@mui/material/Button";

const AddEditRest = () => {
  const { state } = useLocation();
  const id = state ? state.id : null;
  const isAddMode = !id;

  const {
    control,
    handleSubmit,
    register,
    reset,
    formState: { errors },
  }: any = useForm({
    defaultValues: {
      name: "",
      chefName: "",
      pic: "",
      dishes: "",
    },
  });

  function onSubmit(data: any) {
    return isAddMode ? createRest(data) : updateRest(id, data);
  }

  const createRest = (data: any) => {
    // return userService.create(data)
    //     .then(() => {
    //         alertService.success('User added', { keepAfterRouteChange: true });
    //         history.push('.');
    //     })
    //     .catch(alertService.error);
  };

  const updateRest = (id: string, data: any) => {
    // return userService.update(id, data)
    //     .then(() => {
    //         alertService.success('User updated', { keepAfterRouteChange: true });
    //         history.push('..');
    //     })
    //     .catch(alertService.error);
  };

  // const [user, setUser] = useState({});
  // const [showPassword, setShowPassword] = useState(false);

  // useEffect(() => {
  //     if (!isAddMode) {
  //         // get user and set form fields
  //         userService.getById(id).then(user => {
  //             const fields = ['title', 'firstName', 'lastName', 'email', 'role'];
  //             fields.forEach(field => setValue(field, user[field]));
  //             setUser(user);
  //         });
  //     }
  // }, []);

  return (
    <form onSubmit={handleSubmit(onSubmit)} onReset={reset}>
      <h1>{isAddMode ? "Add Restaurant" : "Edit Restaurant"}</h1>
      <div>
        <div>
          <label>Name</label>
          <Controller
            name="name"
            control={control}
            rules={{ required: true, maxLength: 20 }}
            render={() => <Input {...register("name")} />}
          />
          {errors.name?.type === "required" && "First name is required"}
        </div>
        <div>
          <label>Chef Name</label>
          <Controller
            name="chefName"
            control={control}
            rules={{ required: true }}
            render={() => <Input {...register("chefName")} />}
          />
        </div>
        <div className="form-group col-5">
          <label>Picture</label>
          <Controller
            name="pic"
            control={control}
            rules={{ required: true }}
            render={() => <Input {...register("pic")} />}
          />
        </div>
        <div>
          <label>Dishes</label>
          <Controller
            name="dishes"
            control={control}
            rules={{ required: true }}
            render={() => <Input {...register("dishes")} />}
          />
        </div>
      </div>
      <div>
        <Button variant="contained">Save</Button>
      </div>
    </form>
  );
};

export default AddEditRest;
