import "./Menu.scss";
import Modal from "react-modal";
import { useState } from "react";
import { NavLink } from "react-router-dom";

const Menu = () => {
  const [isOpen, setIsOpen] = useState(true);

  // let menuClass = "menu-modal";
  let close = false;

  const closeMenu = () => {
    setIsOpen(!isOpen);
  };

  const changeToModalClose = () => {
    close = true;
    // menuClass = "menu-modal-close";
  };

  return (
    <Modal
      isOpen={isOpen}
      className={!close ? "menu-modal" : "menu-modal-close"}
      ariaHideApp={false}
      onRequestClose={closeMenu}
    >
      <div className="menu-close-container">
        <button className="close-button" onClick={closeMenu} />
      </div>
      <div className="menu">
        <div className="menu-close-line" />
        <NavLink to="/chefs" className="menu-link" onClick={closeMenu}>
          Chefs
        </NavLink>
        <NavLink to="/restaurants" className="menu-link" onClick={closeMenu}>
          All Restaurants
        </NavLink>
        <div className="menu-line" />
        <div className="menu-button">Sign in</div>
        <div className="menu-button">Contact us</div>
        <div className="menu-button">Terms of Use</div>
        {changeToModalClose()}
      </div>
    </Modal>
  );
};

export default Menu;
