import { useState } from "react";
import Modal from "react-modal";
import "./DishModal.scss";
import padKiMao from "../../assets/dishes/pad-ki-mao@3x.png";
import spicyIcon from "../../assets/icons/spicy-icon.png";
import veganIcon from "../../assets/icons/vegan-icon.png";
import padKiMao2 from "../../assets/dishes/pad-ki-mao2.png";
import padKiMao3 from "../../assets/dishes/pad-ki-mao3.png";
import redFarm from "../../assets/dishes/red-farm.png";
import redFarm2 from "../../assets/dishes/red-farm2.png";
import taMaLaKo from "../../assets/dishes/ta-ma-la-ko.png";

const DISHES = [
  {
    pic: padKiMao,
    name: "Pad Ki Mao",
    text: "Shrimps, Glass Noodles, Kemiri Nuts, Shallots, Lemon Grass",
    icons: [spicyIcon],
    price: "₪88",
  },
  {
    pic: taMaLaKo,
    name: "Ta Ma-La-Ko",
    text: "Green Papaya, Mango, Chukka Chili, Mint, Kaffir lime, Cashew",
    icons: [],
    price: "₪98",
  },
  {
    pic: redFarm,
    name: "Red Farm",
    text: "Tofu, Spekkoek Peanuts, Spicy Manis, Pear Yakitori",
    icons: [],
    price: "₪98",
  },
  {
    pic: padKiMao2,
    name: "Pad Ki Mao",
    text: "Shrimps, Glass Noodles, Kemiri Nuts, Shallots, Lemon Grass",
    icons: [spicyIcon, veganIcon],
    price: "₪88",
  },
  {
    pic: redFarm2,
    name: "Red Farm",
    text: "Tofu, Spekkoek Peanuts, Spicy Manis, Pear Yakitori",
    icons: [],
    price: "₪105",
  },
  {
    pic: padKiMao3,
    name: "Pad Ki Mao",
    text: "Shrimps, Glass Noodles, Kemiri Nuts, Shallots, Lemon Grass",
    icons: [],
    price: "₪88",
  },
];

type Props = {
  id: number;
};

const DishModal: React.FC<Props> = ({ id }) => {
  const [isOpen, setIsOpen] = useState(true);
  const [counter, setCounter] = useState<number>(1);

  const dishDetails = (
    <div className="dish-details">
      <div className="dish-name">{DISHES[id].name}</div>
      <div className="dish-desc">{DISHES[id].text}</div>
      <div className="dish-icons">
        {DISHES[id].icons.map((icon) => {
          return <img src={icon} alt="icon" className="dish-icon" />;
        })}
      </div>
      <div className="dish-price-container">
        <div className="dish-line" />
        <div className="price">{DISHES[id].price}</div>
        <div className="dish-line" />
      </div>
    </div>
  );

  const sideOptions = (
    <div className="side-dishes">
      <div>
        <label>
          <input type="radio" name="option" checked={true} />
          White Bread
        </label>
      </div>
      <div>
        <label>
          <input type="radio" name="option" />
          Sticky Rice
        </label>
      </div>
    </div>
  );

  const changeOptions = (
    <div className="change-dishes">
      <div>
        <label>
          <input type="checkbox" name="option" />
          Without Onion
        </label>
      </div>
      <div>
        <label>
          <input type="checkbox" name="option" />
          Without Peanuts
        </label>
      </div>
      <div>
        <label>
          <input type="checkbox" name="option" />
          Less Spicy
        </label>
      </div>
    </div>
  );

  const changeQuantity = (
    <div className="quantity-container">
      <div className="change-counter">-</div>
      <div className="counter">{counter}</div>
      <div className="change-counter">+</div>
    </div>
  );

  return (
    <Modal isOpen={isOpen} className="dish-modal">
      <div className="dish-close-container">
        <button className="close-button" />
      </div>
      <div className="dish-modal-crop">
        <img src={padKiMao} className="dish-modal-pic" />
      </div>
      {dishDetails}
      <div className="choice-header">
        <label className="choice-title">Choose a side</label>
        {/* <div className="side-line" /> */}
      </div>
      {sideOptions}
      <div className="choice-header">
        <label className="choice-title">Changes</label>
        {/* <div className="change-line" /> */}
      </div>
      {changeOptions}
      <div className="choice-header">
        <label className="choice-title">Quantity</label>
        {/* <div className="quantity-line" /> */}
      </div>
      {changeQuantity}
      <div>
        <button className="add-to-bag">ADD TO BAG</button>
      </div>
    </Modal>
  );
};

export default DishModal;
