import PicContainer from "../PicContainer/PicContainer";
import "./Restaurant.scss";
import clock from "../../assets/icons/clock-icon.png";
import spicyIcon from "../../assets/icons/spicy-icon.png";
import veganIcon from "../../assets/icons/vegan-icon.png";
import padKiMao from "../../assets/dishes/pad-ki-mao.png";
import padKiMao2 from "../../assets/dishes/pad-ki-mao2.png";
import padKiMao3 from "../../assets/dishes/pad-ki-mao3.png";
import redFarm from "../../assets/dishes/red-farm.png";
import redFarm2 from "../../assets/dishes/red-farm2.png";
import taMaLaKo from "../../assets/dishes/ta-ma-la-ko.png";
import claroImg from "../../assets/restaurants/claro/claro@3x.png";
import kabKemImg from "../../assets/restaurants/kab-kem/kab-kem@3x.png";
import messaImg from "../../assets/restaurants/messa/messa@3x.png";
import tigerImg from "../../assets/restaurants/tiger-lilly/tiger-lilly@3x.png";
import yaPanImg from "../../assets/restaurants/ya-pan/ya-pan@3x.png";
import nithanImg from "../../assets/restaurants/nithan-thai/nithan-thai@3x.png";

const RESTAURANTS = [
  {
    name: "Claro",
    chefName: "Ran Shmueli",
    pic: claroImg,
  },
  {
    name: "Kab Kem",
    chefName: "Yariv Malili",
    pic: kabKemImg,
  },
  {
    name: "Messa",
    chefName: "Aviv Moshe",
    pic: messaImg,
  },
  {
    name: "Tiger Lilly",
    chefName: "Yanir Green",
    pic: tigerImg,
  },
  {
    name: "Ya Pan",
    chefName: "Yuval Ben Neriah",
    pic: yaPanImg,
  },
  {
    name: "Nithan Thai",
    chefName: "Yanir Green",
    pic: nithanImg,
  },
  {
    name: "Claro",
    chefName: "Ran Shmueli",
    pic: claroImg,
  },
  {
    name: "Kab Kem",
    chefName: "Yariv Malili",
    pic: kabKemImg,
  },
  {
    name: "Messa",
    chefName: "Aviv Moshe",
    pic: messaImg,
  },
  {
    name: "Tiger Lilly",
    chefName: "Yanir Green",
    pic: tigerImg,
  },
];

const DISHES = [
  {
    pic: padKiMao,
    name: "Pad Ki Mao",
    text: "Shrimps, Glass Noodles, Kemiri Nuts, Shallots, Lemon Grass",
    icons: [spicyIcon],
    price: "₪88",
  },
  {
    pic: taMaLaKo,
    name: "Ta Ma-La-Ko",
    text: "Green Papaya, Mango, Chukka Chili, Mint, Kaffir lime, Cashew",
    icons: [],
    price: "₪98",
  },
  {
    pic: redFarm,
    name: "Red Farm",
    text: "Tofu, Spekkoek Peanuts, Spicy Manis, Pear Yakitori",
    icons: [],
    price: "₪98",
  },
  {
    pic: padKiMao2,
    name: "Pad Ki Mao",
    text: "Shrimps, Glass Noodles, Kemiri Nuts, Shallots, Lemon Grass",
    icons: [spicyIcon, veganIcon],
    price: "₪88",
  },
  {
    pic: redFarm2,
    name: "Red Farm",
    text: "Tofu, Spekkoek Peanuts, Spicy Manis, Pear Yakitori",
    icons: [],
    price: "₪105",
  },
  {
    pic: padKiMao3,
    name: "Pad Ki Mao",
    text: "Shrimps, Glass Noodles, Kemiri Nuts, Shallots, Lemon Grass",
    icons: [],
    price: "₪88",
  },
];

type Props = {
  rest: any;
  isDesktop: boolean;
};

const Restaurant: React.FC<Props> = ({ rest, isDesktop }) => {
  const type = isDesktop ? "dish-desk" : "dish";
  {
    console.log(rest);
  }

  return (
    <>
      {rest && (
        <div className="restaurant-container">
          <img src={rest.pic} alt="rest-img" className="rest-img" />
          <div className="rest-name">{rest.name}</div>
          <div className="rest-chef">{rest.chefName}</div>
          <div className="rest-time">
            <img src={clock} alt="clock" className="rest-clock" />
            <div className="time">Open Now</div>
          </div>
          <div className="rest-buttons">
            <div className="rest-button">Breakfast</div>
            <div className="rest-button">Lunch</div>
            <div className="rest-button">Dinner</div>
          </div>
          <div className="dishes">
            {rest.dishes.map((dish: any, index: number) => {
              return (
                <PicContainer
                  key={index}
                  type={type}
                  pic={dish.pic}
                  title={dish.name}
                  text={dish.text}
                  icons={dish.icons}
                  price={dish.price}
                  id={index}
                />
              );
            })}
          </div>
        </div>
      )}
    </>
  );
};

export default Restaurant;
