import PicContainer from "../PicContainer/PicContainer";
import "./DishSlide.scss";
import padKiMao from "../../assets/dishes/pad-ki-mao@2x.png";
import redFarm from "../../assets/dishes/red-farm.png";
import taMaLaKo from "../../assets/dishes/ta-ma-la-ko@2x.png";
import spicyIcon from "../../assets/icons/spicy-icon.png";

const DISHES = [
  {
    pic: padKiMao,
    name: "Pad Ki Mao",
    text: "Shrimps, Glass Noodles, Kemiri Nuts, Shallots, Lemon Grass",
    icons: [spicyIcon],
    price: "₪88",
  },
  {
    pic: taMaLaKo,
    name: "Ta Ma-La-Ko",
    text: "Green Papaya, Mango, Chukka Chili, Mint, Kaffir lime, Cashew",
    icons: [],
    price: "₪98",
  },
  {
    pic: redFarm,
    name: "Red Farm",
    text: "Tofu, Spekkoek Peanuts, Spicy Manis, Pear Yakitori",
    icons: [],
    price: "₪98",
  },
];

type Props = {
  dishes: any[];
};

const DishSlide: React.FC<Props> = ({ dishes }) => {
  return (
    <div className="dish-slide-container">
      <div className="dish-slide-title">SIGNATURE DISHES OF :</div>
      <div className="dish-slide-name">Tiger Lilly</div>
      <div className="dish-slide">
        {dishes.map((dish, index) => {
          return (
            <PicContainer
              key={index}
              type="dish-carusel"
              pic={dish.pic}
              title={dish.name}
              text={dish.text}
              icons={dish.icons}
              price={dish.price}
              id={index}
            />
          );
        })}
      </div>
    </div>
  );
};

export default DishSlide;
