import "./PicContainer.scss";
import path from "../../assets/dishes/pad-ki-mao2.png";
import path2 from "../../assets/icons/spicy-icon.png";

type Props = {
  type: string;
  pic: string;
  title: string;
  text: string;
  icon: string;
  price: string;
};

const PicContainer: React.FC<Props> = ({
  type,
  pic,
  title,
  text,
  icon,
  price,
}) => {
  const containerClass = type + "-container";
  const titleClass = type + "-title";
  const textClass = type + "-text";

  return (
    <div className={containerClass}>
      <img src={path} alt="pic" />
      <div className={titleClass}>{title}</div>
      <div className={textClass}>{text}</div>
      {/* {icon && price && ( */}
      <div className="dish-info">
        <img src={path2} alt="icon" />
        <div className="dish-price">
          <div className="line" />
          {price}
          <div className="line" />
        </div>
      </div>
      {/* )} */}
    </div>
  );
};

export default PicContainer;
