import React, { useEffect, useState } from "react";
import { Controller, useForm } from "react-hook-form";
import { useLocation } from "react-router";
import Input from "@mui/material/Input";
import Button from "@mui/material/Button";
import axios from "axios";
import qs from "querystring";
import "./AddEditRest.scss";

const AddEditRest = () => {
  const { state } = useLocation();
  const id = state ? state.id : null;
  const isAddMode = !id;

  const {
    control,
    handleSubmit,
    register,
    reset,
    formState: { errors },
  }: any = useForm({
    defaultValues: {
      name: "",
      chefName: "",
      pic: "",
      dishes: "",
    },
    mode: "all",
  });

  const onSubmit = (rest: any) => {
    return isAddMode ? createRest(rest) : updateRest(id, rest);
  };

  const createRest = (rest: any) => {
    (async () => {
      const headers = {
        "Content-Type": "application/x-www-form-urlencoded",
      };

      const res = await axios.post(
        "http://localhost:3001/api/v1/restaurants",
        qs.stringify(rest),
        { headers: headers }
      );
    })();
  };

  const updateRest = (id: string, rest: any) => {
    (async () => {
      // const headers = {
      //   "Content-Type": "application/x-www-form-urlencoded",
      // };

      const res = await axios.put(
        "http://localhost:3001/api/v1/restaurants",
        qs.stringify(rest)
        // { headers: headers }
      );
    })();
  };

  // const [user, setUser] = useState({});
  // const [showPassword, setShowPassword] = useState(false);

  // useEffect(() => {
  //     if (!isAddMode) {
  //         // get user and set form fields
  //         userService.getById(id).then(user => {
  //             const fields = ['title', 'firstName', 'lastName', 'email', 'role'];
  //             fields.forEach(field => setValue(field, user[field]));
  //             setUser(user);
  //         });
  //     }
  // }, []);

  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      onReset={reset}
      className="rest-form"
    >
      <h1>{isAddMode ? "Add Restaurant" : "Edit Restaurant"}</h1>
      <div className="input-container">
        <div>
          <div className="input-field">
            <label>Name</label>
            <Controller
              name="name"
              control={control}
              rules={{ required: true, maxLength: 20 }}
              render={() => <Input {...register("name")} />}
            />
          </div>
          {errors.name && errors.name.type === "required" && (
            <span role="alert" className="input-error">
              Name is required
            </span>
          )}
          {errors.name && errors.name.type === "maxLength" && (
            <span role="alert" className="input-error">
              Name length should be less than 20 characters
            </span>
          )}
        </div>
        <div>
          <div className="input-field">
            <label>Chef Name</label>
            <Controller
              name="chefName"
              control={control}
              rules={{ required: true }}
              render={() => <Input {...register("chefName")} />}
            />
          </div>
          {errors.chefName && errors.chefName.type === "required" && (
            <span role="alert" className="input-error">
              Chef name is required
            </span>
          )}
        </div>
        <div>
          <div className="input-field">
            <label>Picture</label>
            <Controller
              name="pic"
              control={control}
              rules={{ required: true }}
              render={() => <Input {...register("pic")} />}
            />
          </div>
          {errors.pic && errors.pic.type === "required" && (
            <span role="alert" className="input-error">
              Picture is required
            </span>
          )}
        </div>
        <div>
          <div className="input-field">
            <label>Dishes</label>
            <Controller
              name="dishes"
              control={control}
              render={() => <Input {...register("dishes")} />}
            />
          </div>
        </div>
      </div>
      <div className="input-button-cont">
        <Button type="submit" variant="contained">
          Save
        </Button>
      </div>
    </form>
  );
};

export default AddEditRest;
