import React from "react";
import PicContainer from "../PicContainer/PicContainer";
import "./RestaurantsHome.scss";
import claroImg from "../../assets/restaurants/claro/claro@2x.png";
import luminaImg from "../../assets/restaurants/lumina/lumina.png";
import tigerImg from "../../assets/restaurants/tiger-lilly/tiger-lilly@2x.png";
import arrow from "../../assets/icons/all-restaurants-arrow.png";

const RESTAURANTS = [
  {
    name: "Claro",
    chefName: "Ran Shmueli",
    pic: claroImg,
  },
  {
    name: "Lumina",
    chefName: "Meir Adoni",
    pic: luminaImg,
  },
  {
    name: "Tiger Lilly",
    chefName: "Yanir Green",
    pic: tigerImg,
  },
];

const RestaurantsHome = () => {
  return (
    <div className="rests-home-container">
      <div className="rests-home-title">
        THE POPULAR RESTAURANTS IN EPICURE :
      </div>
      <div className="rests-home">
        {RESTAURANTS.map((rest, index) => {
          return (
            <PicContainer
              key={index}
              type="rest-home"
              pic={rest.pic}
              title={rest.name}
              text={rest.chefName}
              icons={[]}
              price=""
              id={index}
            />
          );
        })}
      </div>
      <div>
        <button className="rests-home-button">All Restaurants {">>"}</button>
        <img src={arrow} alt="arrow" />
      </div>
    </div>
  );
};

export default RestaurantsHome;
