import React from "react";
import PicContainer from "../PicContainer/PicContainer";
import "./Restaurants.scss";
import claroImg from "../../assets/restaurants/claro/claro.png";
import kabKemImg from "../../assets/restaurants/kab-kem/kab-kem.png";
import messaImg from "../../assets/restaurants/messa/messa.png";
import tigerImg from "../../assets/restaurants/tiger-lilly/tiger-lilly.png";
import yaPanImg from "../../assets/restaurants/ya-pan/ya-pan.png";
import nithanImg from "../../assets/restaurants/nithan-thai/nithan-thai.png";

const RESTAURANTS = [
  {
    name: "Claro",
    chefName: "Ran Shmueli",
    pic: claroImg,
  },
  {
    name: "Kab Kem",
    chefName: "Yariv Malili",
    pic: kabKemImg,
  },
  {
    name: "Messa",
    chefName: "Aviv Moshe",
    pic: messaImg,
  },
  {
    name: "Tiger Lilly",
    chefName: "Yanir Green",
    pic: tigerImg,
  },
  {
    name: "Ya Pan",
    chefName: "Yuval Ben Neriah",
    pic: yaPanImg,
  },
  {
    name: "Nithan Thai",
    chefName: "Yanir Green",
    pic: nithanImg,
  },
  {
    name: "Claro",
    chefName: "Ran Shmueli",
    pic: claroImg,
  },
  {
    name: "Kab Kem",
    chefName: "Yariv Malili",
    pic: kabKemImg,
  },
  {
    name: "Messa",
    chefName: "Aviv Moshe",
    pic: messaImg,
  },
  {
    name: "Tiger Lilly",
    chefName: "Yanir Green",
    pic: tigerImg,
  },
];

type Props = {
  isDesktop: boolean;
};

const Restaurants: React.FC<Props> = ({ isDesktop }) => {
  const type = isDesktop ? "rest-home" : "rest";

  return (
    <div className="restaurants-container">
      {!isDesktop && <div className="rests-title">RESTAURANTS</div>}
      <div className="rests-buttons">
        <div className="rests-button">All</div>
        <div className="rests-button">New</div>
        <div className="rests-button">Most Popular</div>
        <div className="rests-button">Open Now</div>
      </div>
      <div className="rests">
        {RESTAURANTS.map((rest, index) => {
          return (
            <PicContainer
              key={index}
              type={type}
              pic={rest.pic}
              title={rest.name}
              text={rest.chefName}
              icons={[]}
              price=""
              id={index}
            />
          );
        })}
      </div>
    </div>
  );
};

export default Restaurants;
