import React from "react";

//import chefImg from "../../assets/chefs/yossi-shitrit.png";
import "./ChefOfTheWeek.scss";
import ChefOfTheWeekSlide from "./ChefOfTheWeekSlide";

type Props = {
  chefs: any[];
};

const ChefOfTheWeek: React.FC<Props> = ({ chefs }) => {
  const title = (
    <span className="chef-title">
      CHEF OF THE WEEK:
      <br />
    </span>
  );

  const text = (
    <div className="chef-text">
      {/* Chef Yossi Shitrit has been living and breathing his culinary dreams for
      more than two decades, including running the kitchen in his first
      restaurant, the fondly-remembered Violet, located in Moshav Udim.
      Shitrit's creativity and culinary acumen born of long experience are
      expressed in the every detail of each and every dish. */}
      {chefs.map((chef) => {
        return <div>chef.description</div>;
      })}
    </div>
  );

  return (
    <div className="chef-container">
      {title}
      <div className="chef-pic-and-text">
        <div className="chef-pic-container">
          <div className="chef-name">{chefs[0].name}</div>
          <img src={chefs[0].pic} alt="yossi-shitrit" className="chef-pic" />
        </div>
        {text}
      </div>

      <ChefOfTheWeekSlide />
    </div>
  );
};

export default ChefOfTheWeek;
