import Restaurant from "../components/Restaurants/Restaurant";
import { useMediaQuery } from "react-responsive";
import { useEffect, useState } from "react";
import axios from "axios";
import { useLocation } from "react-router";

const RestaurantPage = () => {
  //const id = Number(window.location.pathname.split("/")[2]);
  const isDesktopScreen = useMediaQuery({ query: "(min-width: 600px)" });
  const [restaurant, setRestaurant] = useState<any>();

  useEffect(() => {
    (async () => {
      const { state } = useLocation();
      const path = "http://localhost:3001/restaurants/" + state.id;

      const rest = await axios(path);
      setRestaurant(rest.data);
    })();
  }, []);

  return <Restaurant res={restaurant} isDesktop={isDesktopScreen} />;
  //return <div></div>;
};

export default RestaurantPage;
